import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import EmojiPicker from 'emoji-picker-react';

const Journal = () => {
  const [text, setText] = useState('');
  const [date, setDate] = useState(new Date().toISOString().substring(0, 10));
  const [showPicker, setShowPicker] = useState(false); // ðŸ‘ˆ for toggling picker
  const userId = '123'; // Replace with actual logged-in user ID

  useEffect(() => {
    const fetchEntry = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/journal/${userId}/${date}`);
        if (response.data && response.data.text) {
          setText(response.data.text);
        }
      } catch (err) {
        console.error('Error fetching journal entry', err);
      }
    };

    fetchEntry();
  }, [date]);

  const handleSave = async () => {
    try {
      await axios.post('http://localhost:5000/api/journal', {
        user: userId,
        date,
        text
      });
      alert('Journal saved!');
    } catch (err) {
      console.error('Error saving journal entry', err);
    }
  };

  const handleEmojiClick = (emojiData) => {
    setText((prev) => prev + emojiData.emoji); // ðŸ‘ˆ Append emoji to text
  };

  return (
    <div className="journal-page">
      <h1>My daily journal</h1>

      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
      />

      <div style={{ position: 'relative', marginBottom: '1rem' }}>
        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Write your thoughts here..."
          rows="10"
          cols="50"
        />
        <div style={{ marginTop: '0.5rem' }}>
          <button onClick={() => setShowPicker(!showPicker)}>
            {showPicker ? 'Hide Emojis' : 'ðŸ˜€ Add Emoji'}
          </button>
        </div>

        {showPicker && (
          <div style={{ position: 'absolute', zIndex: 1000 }}>
            <EmojiPicker onEmojiClick={handleEmojiClick} />
          </div>
        )}
      </div>

      <button onClick={handleSave}>Save</button>
    </div>
  );
};

export default Journal;
