import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Picker from '@emoji-mart/react';
import data from '@emoji-mart/data';
import '@emoji-mart/react/dist/app.css';

const Journal = () => {
  const [text, setText] = useState('');
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10));
  const [showPicker, setShowPicker] = useState(false);
  const userId = '123'; // your user ID

  useEffect(() => {
    const fetchEntry = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/journal/${userId}/${date}`);
        if (res.data && res.data.text) setText(res.data.text);
      } catch (e) {
        console.error(e);
      }
    };
    fetchEntry();
  }, [date]);

  const handleSave = async () => {
    try {
      await axios.post('http://localhost:5000/api/journal', { user: userId, date, text });
      alert('Journal saved!');
    } catch (e) {
      console.error(e);
    }
  };

  const handleEmojiSelect = (emoji) => {
    setText(prev => prev + emoji.native);
  };

  return (
    <div className="journal-container">
      <h1>My Daily Journal</h1>
      <input type="date" value={date} onChange={e => setDate(e.target.value)} />
      <textarea
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Write your thoughts here..."
        rows={10}
      />
      <button onClick={() => setShowPicker(!showPicker)}>
        {showPicker ? 'Hide Emojis' : 'ðŸ˜€ Add Emoji'}
      </button>

      {showPicker && (
        <div style={{ marginTop: '10px' }}>
          <Picker data={data} onEmojiSelect={handleEmojiSelect} />
        </div>
      )}

      <button onClick={handleSave}>Save</button>
    </div>
  );
};

export default Journal;
