import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import headerImage from '../assets/Routines-illustration.png';
import './Routines.css';

const Routines = () => {
  const { user } = useAuth();
  const [text, setText] = useState('');
  const [date, setDate] = useState(new Date().toISOString().substring(0, 10));
  const [tasks, setTasks] = useState([
    { id: 1, text: 'Wake up early', checked: false },
    { id: 2, text: 'Exercise', checked: false },
    { id: 3, text: 'Read or journal', checked: false },
  ]);

  useEffect(() => {
    if (!user) return;

    axios
      .get(`/api/routines/${user._id}/${date}`)
      .then(res => setText(res.data?.text || ''))
      .catch(err => console.error(err));
  }, [date, user]);

  const handleSave = () => {
    if (!user) return;

    axios
      .post('/api/routines', {
        user: user._id,
        date,
        text,
        tasks,
      })
      .then(() => alert('Routine saved!'))
      .catch(err => console.error(err));
  };

  const handleTaskChange = (id) => {
    setTasks(prev =>
      prev.map(task =>
        task.id === id ? { ...task, checked: !task.checked } : task
      )
    );
  };

  return (
    <div className="routines-page">
      <header className="page-header-image">
        <img src={headerImage} alt="Header" className="full-width-image" />
      </header>
      <h1>Daily Routine ðŸ“†</h1>
      <p className="routine-description">
        Plan your day and set your intentions. What do you want to achieve today?
      </p>
      <input type="date" value={date} onChange={e => setDate(e.target.value)} />

      <div className="routine-columns">
        <div className="routine-left">
          <h2>To-do List</h2>
          <ul className="routine-list">
            {tasks.map(task => (
              <li key={task.id}>
                <label>
                  <input
                    type="checkbox"
                    checked={task.checked}
                    onChange={() => handleTaskChange(task.id)}
                  />
                  {task.text}
                </label>
              </li>
            ))}
          </ul>
        </div>

        <div className="routine-right">
          <h2>Notes</h2>
          <textarea
            value={text}
            onChange={e => setText(e.target.value)}
            placeholder="Whatâ€™s your plan today?"
            rows={10}
            cols={50}
          />
        </div>
      </div>

      <button className="routine-button" onClick={handleSave}>Save</button>
    </div>
  );
};

export default Routines;
