import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import headerImage from '../assets/Routines-illustration.png';

const Routines = () => {
  const { user } = useAuth();
  const [todos, setTodos] = useState(
    Array.from({ length: 10 }, () => ({ checked: false, text: '' }))
  );
  const [notes, setNotes] = useState('');
  const [date, setDate] = useState(new Date().toISOString().substring(0, 10));

  useEffect(() => {
    if (!user) return;

    // Load routines or todos from backend if you want
    // axios.get(`/api/routines/${user._id}/${date}`).then(...)

  }, [date, user]);

  const handleCheckboxChange = (index) => {
    const updatedTodos = [...todos];
    updatedTodos[index].checked = !updatedTodos[index].checked;
    setTodos(updatedTodos);
  };

  const handleTaskTextChange = (index, value) => {
    const updatedTodos = [...todos];
    updatedTodos[index].text = value;
    setTodos(updatedTodos);
  };

  const handleSave = () => {
    if (!user) return;

    axios
      .post('/api/routines', {
        user: user._id,
        date,
        todos,
        notes,
      })
      .then(() => alert('Routine saved!'))
      .catch(err => console.error(err));
  };

  return (
    <div className="routines-page">
      <header className="page-header-image">
        <img src={headerImage} alt="Header" className="full-width-image" />
      </header>

      <h1>Daily Routine ðŸ“†</h1>
      <p className="routine-description">
        Plan your day and set your intentions. What do you want to achieve today?
      </p>

      <input type="date" value={date} onChange={e => setDate(e.target.value)} />

      <div className="todo-list">
        {todos.map((todo, index) => (
          <div className="task-input-wrapper" key={index}>
            <input
              type="checkbox"
              checked={todo.checked}
              onChange={() => handleCheckboxChange(index)}
            />
            <input
              type="text"
              value={todo.text}
              maxLength={100}
              onChange={(e) => handleTaskTextChange(index, e.target.value)}
              placeholder="Enter task"
            />
          </div>
        ))}
      </div>

      <textarea
        value={notes}
        onChange={e => setNotes(e.target.value)}
        placeholder="Notes"
        rows={5}
      />

      <button onClick={handleSave}>Save</button>
    </div>
  );
};

export default Routines;
