import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import headerImage from '../assets/Routines-illustration.png';
import './Routines.css';

const Routines = () => {
  const { user } = useAuth();
  const [text, setText] = useState('');
  const [date, setDate] = useState(new Date().toISOString().substring(0, 10));

  const [tasks, setTasks] = useState(() =>
    Array.from({ length: 10 }, (_, i) => ({
      id: i + 1,
      text: '',
      checked: false,
    }))
  );

  useEffect(() => {
    if (!user) return;

    axios
      .get(`/api/routines/${user._id}/${date}`)
      .then((res) => {
        const data = res.data || {};
        setText(data.text || '');
        setTasks(data.tasks || tasks);
      })
      .catch((err) => console.error(err));
    // eslint-disable-next-line
  }, [date, user]);

  const handleTaskTextChange = (id, newText) => {
    setTasks((prev) =>
      prev.map((task) =>
        task.id === id ? { ...task, text: newText } : task
      )
    );
  };

  const handleTaskCheck = (id) => {
    setTasks((prev) =>
      prev.map((task) =>
        task.id === id ? { ...task, checked: !task.checked } : task
      )
    );
  };

  const handleSave = () => {
    if (!user) return;

    axios
      .post('/api/routines', {
        user: user._id,
        date,
        text,
        tasks,
      })
      .then(() => alert('Routine saved!'))
      .catch((err) => console.error(err));
  };

  return (
    <div className="routines-page">
      <header className="page-header-image">
        <img src={headerImage} alt="Header" className="full-width-image" />
      </header>
      <h1>Daily Routine ðŸ“†</h1>
      <p className="routine-description">
        Plan your day and set your intentions. What do you want to achieve today?
      </p>
      <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />

      <div className="routine-columns">
        <div className="routine-left">
          <h2>To-do List</h2>
          <ul className="routine-list">
            {tasks.map((task) => (
              <li key={task.id} className="task-row">
                <input
                  type="checkbox"
                  checked={task.checked}
                  onChange={() => handleTaskCheck(task.id)}
                />
                <input
                  type="text"
                  value={task.text}
                  onChange={(e) => handleTaskTextChange(task.id, e.target.value)}
                  placeholder={`Task ${task.id}`}
                  className="task-input"
                />
              </li>
            ))}
          </ul>
        </div>

        <div className="routine-right">
          <h2>Notes</h2>
          <textarea
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Whatâ€™s your plan today?"
            rows={10}
          />
        </div>
      </div>

      <button className="routine-button" onClick={handleSave}>Save</button>
    </div>
  );
};

export default Routines;
