import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Picker from '@emoji-mart/react';
import data from '@emoji-mart/data';


const Journal = () => {
  const [text, setText] = useState('');
  const [date, setDate] = useState(new Date().toISOString().substring(0, 10));
  const [showPicker, setShowPicker] = useState(false);
  const userId = '123'; // Replace with actual logged-in user ID

  useEffect(() => {
    const fetchEntry = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/journal/${userId}/${date}`);
        if (response.data && response.data.text) {
          setText(response.data.text);
        }
      } catch (err) {
        console.error('Error fetching journal entry', err);
      }
    };

    fetchEntry();
  }, [date]);

  const handleSave = async () => {
    try {
      await axios.post('http://localhost:5000/api/journal', {
        user: userId,
        date,
        text
      });
      alert('Journal saved!');
    } catch (err) {
      console.error('Error saving journal entry', err);
    }
  };

  const handleEmojiSelect = (emoji) => {
    setText((prevText) => prevText + emoji.native);
  };

  return (
    <div className="journal-container">
      <h1>My Daily Journal</h1>

      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        className="date-input"
      />

      <textarea
        className="journal-textarea"
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Write your thoughts here..."
        rows="10"
        cols="50"
      />

      <button
        className="emoji-toggle-button"
        onClick={() => setShowPicker(!showPicker)}
      >
        {showPicker ? 'Hide Emojis' : 'ðŸ˜€ Add Emoji'}
      </button>

      {showPicker && (
        <div className="emoji-picker-container">
          <Picker onEmojiSelect={handleEmojiSelect} />
        </div>
      )}

      <button onClick={handleSave} className="save-button">
        Save
      </button>
    </div>
  );
};

export default Journal;
