import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import headerImage from '../assets/Gratitude-illustration.png';
const Gratitude = () => {
  const { user } = useAuth();
  const [text, setText] = useState('');
  const [date, setDate] = useState(new Date().toISOString().substring(0, 10));

  useEffect(() => {
    if (!user) return;

    axios
      .get(`/api/gratitude/${user._id}/${date}`)
      .then(res => setText(res.data?.text || ''))
      .catch(err => console.error(err));
  }, [date, user]);

  const handleSave = () => {
    if (!user) return;

    axios
      .post('/api/gratitude', {
        user: user._id,
        date,
        text,
      })
      .then(() => alert('Gratitude saved!'))
      .catch(err => console.error(err));
  };

  return (
    <div className="gratitute-page">
      <header className="page-header-image">
        <img src={headerImage} alt="Header" className="full-width-image" />
      </header>
      <h1>Three things that made me smile today ðŸ˜Š</h1>
      <input type="date" value={date} onChange={e => setDate(e.target.value)} />
      <textarea
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="What are you grateful for today?"
        rows={10}
        cols={50}
      />
      <button onClick={handleSave}>Save</button>
    </div>
  );
};

export default Gratitude;
